mapper.select:
SELECT DISTINCT 

FROM bg_daily a

WHERE a.id = #{id}

SELECT DISTINCT
FROM bg_daily a
a.del_flag = #{DEL_FLAG_NORMAL}
and a.create_by = #{createBy.id}
AND a.ri_date = #{riDate}
AND a.today_remark LIKE '%'||#{todayRemark}||'%' '%'+#{todayRemark}+'%' concat('%',#{todayRemark},'%')
AND a.tomorrow_plan LIKE '%'||#{tomorrowPlan}||'%' '%'+#{tomorrowPlan}+'%' concat('%',#{tomorrowPlan},'%')
AND d.mobile_number = #{bgDailyContacterList[0].mobileNumber}
AND d.name LIKE concat('%',#{bgDailyContacterList[0].name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT DISTINCT 

FROM bg_daily a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bg_daily SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
