mapper.select:
SELECT 

FROM bg_express a

WHERE a.id = #{id}

SELECT 

FROM bg_express a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.dilivery_date = #{diliveryDate}


		AND a.file LIKE 
			'%'||#{file}||'%'
			'%'+#{file}+'%'
			concat('%',#{file},'%')


		AND a.address LIKE 
			'%'||#{address}||'%'
			'%'+#{address}+'%'
			concat('%',#{address},'%')


		AND a.phone_number = #{phoneNumber}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM bg_express a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bg_express SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
