mapper.select:
SELECT 

FROM jy_customer_follow a

WHERE a.id = #{id}

SELECT 

FROM jy_customer_follow a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.hid = #{hid}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM jy_customer_follow a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
UPDATE jy_customer_follow SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE hid = #{hid}
